            467 => 
            array (
                'id' => 40535,
                'capitulo_id' => 1522,
                'pericopa_id' => 5283,
                'num_versiculo' => 17,
                'contenido' => 'Y ahora no os doy más en esta ocasión. Amén.',
                'referencia' => 'Doctrina y Convenios 94:17',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
