            111 => 
            array (
                'id' => 40708,
                'capitulo_id' => 1529,
                'pericopa_id' => 5089,
                'num_versiculo' => 17,
                'contenido' => 'Sión no será quitada de su lugar, a pesar de que sus hijos han sido esparcidos.',
                'referencia' => 'Doctrina y Convenios 101:17',
