            185 => 
            array (
                'id' => 40788,
                'capitulo_id' => 1529,
                'pericopa_id' => 5095,
                'num_versiculo' => 91,
                'contenido' => 'sí, en las tinieblas de afuera, donde es el lloro, el llanto y el crujir de dientes.',
                'referencia' => 'Doctrina y Convenios 101:91',
