            187 => 
            array (
                'id' => 40790,
                'capitulo_id' => 1529,
                'pericopa_id' => 5095,
                'num_versiculo' => 93,
                'contenido' => 'Lo que os he dicho tiene que verificarse, para que todos los hombres queden sin excusa;',
                'referencia' => 'Doctrina y Convenios 101:93',
                'imagen' => '',
                'pie_imagen' => '',
