            198 => 
            array (
                'id' => 40802,
                'capitulo_id' => 1530,
                'pericopa_id' => 5203,
                'num_versiculo' => 1,
                'contenido' => 'ORSON HYDE,',
                'referencia' => 'Doctrina y Convenios 102:1',
