            265 => 
            array (
                'id' => 40877,
                'capitulo_id' => 1531,
                'pericopa_id' => 5192,
                'num_versiculo' => 33,
                'contenido' => 'Y si no podéis conseguir trescientos, procurad diligentemente obtener por ventura cien.',
                'referencia' => 'Doctrina y Convenios 103:33',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:46',
