            ),
            266 => 
            array (
                'id' => 40878,
                'capitulo_id' => 1531,
                'pericopa_id' => 5192,
                'num_versiculo' => 34,
                'contenido' => 'Mas de cierto os digo, un mandamiento os doy, que no subiréis a la tierra de Sión hasta que hayáis reunido a cien de los de la fuerza de mi casa, para que suban con vosotros a la tierra de Sión.',
                'referencia' => 'Doctrina y Convenios 103:34',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:46',
                'updated_at' => '2024-03-12 15:25:46',
            ),
            267 => 
            array (
                'id' => 40879,
                'capitulo_id' => 1531,
                'pericopa_id' => 5192,
                'num_versiculo' => 35,
                'contenido' => 'Así pues, como os dije, pedid y recibiréis; orad sinceramente a fin de que quizá mi siervo José Smith, hijo, vaya con vosotros, y presida en medio de mi pueblo, y organice mi reino en la tierra consagrada, y establezca a los hijos de Sión sobre las leyes y los mandamientos que se os han dado y que se os darán.',
                'referencia' => 'Doctrina y Convenios 103:35',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:46',
                'updated_at' => '2024-03-12 15:25:46',
            ),
            268 => 
            array (
                'id' => 40880,
                'capitulo_id' => 1531,
                'pericopa_id' => 5192,
                'num_versiculo' => 36,
                'contenido' => 'Toda victoria y toda gloria os es realizada mediante vuestra diligencia, fidelidad y oraciones de fe.',
                'referencia' => 'Doctrina y Convenios 103:36',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:46',
                'updated_at' => '2024-03-12 15:25:46',
            ),
            269 => 
            array (
                'id' => 40881,
                'capitulo_id' => 1531,
                'pericopa_id' => 5192,
                'num_versiculo' => 37,
                'contenido' => 'Viaje mi siervo Parley P. Pratt con mi siervo José Smith, hijo.',
                'referencia' => 'Doctrina y Convenios 103:37',
                'imagen' => '',
                'pie_imagen' => '',
