            array (
                'id' => 40882,
                'capitulo_id' => 1531,
                'pericopa_id' => 5192,
                'num_versiculo' => 38,
                'contenido' => 'Salga mi siervo Lyman Wight con mi siervo Sidney Rigdon.',
                'referencia' => 'Doctrina y Convenios 103:38',
                'imagen' => '',
