            271 => 
            array (
                'id' => 40883,
                'capitulo_id' => 1531,
                'pericopa_id' => 5192,
                'num_versiculo' => 39,
                'contenido' => 'Acompañe mi siervo Hyrum Smith a mi siervo Frederick G. Williams.',
                'referencia' => 'Doctrina y Convenios 103:39',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:46',
