            array (
                'id' => 41008,
                'capitulo_id' => 1533,
                'pericopa_id' => 5178,
                'num_versiculo' => 26,
                'contenido' => 'He aquí, os digo, mis amigos, de esta manera hallaréis gracia ante los ojos del pueblo, hasta que el ejército de Israel llegue a ser muy grande.',
                'referencia' => 'Doctrina y Convenios 105:26',
