            array (
                'id' => 41012,
                'capitulo_id' => 1533,
                'pericopa_id' => 5179,
                'num_versiculo' => 29,
                'contenido' => 'Porque es mi voluntad que se compren estos terrenos y, ya adquiridos, que mis santos los posean de acuerdo con las leyes de consagración que he dado.',
                'referencia' => 'Doctrina y Convenios 105:29',
                'imagen' => '',
