            array (
                'id' => 41067,
                'capitulo_id' => 1535,
                'pericopa_id' => 5080,
                'num_versiculo' => 24,
                'contenido' => 'Y constituyen un quórum, igual en autoridad y poder que los tres presidentes ya mencionados.',
