            100 => 
            array (
                'id' => 41254,
                'capitulo_id' => 1538,
                'pericopa_id' => 5284,
                'num_versiculo' => 5,
                'contenido' => 'He aquí, vuestros pecados os son perdonados; os halláis limpios delante de mí; por tanto, alzad la cabeza y regocijaos.',
                'referencia' => 'Doctrina y Convenios 110:5',
