            103 => 
            array (
                'id' => 41257,
                'capitulo_id' => 1538,
                'pericopa_id' => 5284,
                'num_versiculo' => 8,
                'contenido' => 'Sí, apareceré a mis siervos y les hablaré con mi propia voz, si mi pueblo guarda mis mandamientos y no profana esta santa casa.',
                'referencia' => 'Doctrina y Convenios 110:8',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:46',
                'updated_at' => '2024-03-12 15:25:46',
