            array (
                'id' => 41271,
                'capitulo_id' => 1539,
                'pericopa_id' => 5370,
                'num_versiculo' => 3,
                'contenido' => 'Conviene, pues, que lleguéis a conocer a hombres de esta ciudad, según seáis guiados y os sea indicado.',
                'referencia' => 'Doctrina y Convenios 111:3',
