            array (
                'id' => 41273,
                'capitulo_id' => 1539,
                'pericopa_id' => 5370,
                'num_versiculo' => 5,
                'contenido' => 'No os preocupéis por vuestras deudas, porque os daré el poder de pagarlas.',
                'referencia' => 'Doctrina y Convenios 111:5',
                'imagen' => '',
                'pie_imagen' => '',
