            117 => 
            array (
                'id' => 41275,
                'capitulo_id' => 1539,
                'pericopa_id' => 5371,
                'num_versiculo' => 6,
                'contenido' => 'No os inquietéis tocante a Sión, porque obraré misericordiosamente con ella.',
                'referencia' => 'Doctrina y Convenios 111:6',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:46',
                'updated_at' => '2024-03-12 15:25:46',
            ),
            118 => 
            array (
                'id' => 41276,
                'capitulo_id' => 1539,
                'pericopa_id' => 5371,
                'num_versiculo' => 7,
                'contenido' => 'Permaneced en este lugar y en las regiones circunvecinas;',
                'referencia' => 'Doctrina y Convenios 111:7',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:46',
                'updated_at' => '2024-03-12 15:25:46',
            ),
            119 => 
            array (
                'id' => 41277,
                'capitulo_id' => 1539,
                'pericopa_id' => 5371,
                'num_versiculo' => 8,
                'contenido' => 'y el sitio donde es mi voluntad que principalmente os detengáis, os será manifestado por la paz y el poder de mi Espíritu que fluirá hacia vosotros.',
                'referencia' => 'Doctrina y Convenios 111:8',
