                'capitulo_id' => 1540,
                'pericopa_id' => 5216,
                'num_versiculo' => 9,
                'contenido' => 'Tu voz será un reproche al transgresor; y ante tu reprensión cese la perversidad de la lengua del calumniador.',
                'referencia' => 'Doctrina y Convenios 112:9',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:46',
                'updated_at' => '2024-03-12 15:25:46',
