            173 => 
            array (
                'id' => 41341,
                'capitulo_id' => 1543,
                'pericopa_id' => 5262,
                'num_versiculo' => 5,
                'contenido' => 'De cierto os digo a todos: Levantaos y brillad, para que vuestra luz sea un estandarte a las naciones;',
                'referencia' => 'Doctrina y Convenios 115:5',
                'imagen' => '',
