                'pericopa_id' => 5026,
                'num_versiculo' => 66,
                'contenido' => 'ni les será permitido recibir menos de cincuenta dólares de una sola persona, por una acción de esa casa.',
                'referencia' => 'Doctrina y Convenios 124:66',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
