            358 => 
            array (
                'id' => 41554,
                'capitulo_id' => 1552,
                'pericopa_id' => 5026,
                'num_versiculo' => 68,
                'contenido' => 'y recibirá acciones de esa casa en proporción a la cantidad que entregue en sus manos; pero si no entrega nada en sus manos, no recibirá acciones de la casa.',
                'referencia' => 'Doctrina y Convenios 124:68',
