            array (
                'id' => 41559,
                'capitulo_id' => 1552,
                'pericopa_id' => 5026,
                'num_versiculo' => 73,
                'contenido' => 'Y también hay otros que desean saber mi voluntad concerniente a ellos, porque me lo han pedido.',
                'referencia' => 'Doctrina y Convenios 124:73',
