            array (
                'id' => 41618,
                'capitulo_id' => 1552,
                'pericopa_id' => 5029,
                'num_versiculo' => 129,
                'contenido' => 'Son: Heber C. Kimball, Parley P. Pratt, Orson Pratt, Orson Hyde, William Smith, John Taylor, John E. Page, Wilford Woodruff, Willard Richards, George A. Smith;',
                'referencia' => 'Doctrina y Convenios 124:129',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:47',
                'updated_at' => '2024-03-12 15:25:47',
            ),
