            array (
                'id' => 41619,
                'capitulo_id' => 1552,
                'pericopa_id' => 5029,
                'num_versiculo' => 130,
                'contenido' => 'a David Patten lo he tomado para mí; he aquí, nadie le quita su sacerdocio; pero de cierto os digo, se puede nombrar a otro al mismo llamamiento.',
                'referencia' => 'Doctrina y Convenios 124:130',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:47',
