                'capitulo_id' => 1572,
                'pericopa_id' => 2650,
                'num_versiculo' => 8,
                'contenido' => 'Y la mujer dijo a la serpiente: Podemos comer del fruto de los árboles del jardín;',
                'referencia' => 'Moisés 4:8',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:47',
                'updated_at' => '2024-03-12 15:25:47',
