            7 => 
            array (
                'id' => 42268,
                'capitulo_id' => 1574,
                'pericopa_id' => 2674,
                'num_versiculo' => 22,
                'contenido' => 'Y ésta es la genealogía de los hijos de Adán, que fue el hijo de Dios, con el cual Dios mismo conversó.',
                'referencia' => 'Moisés 6:22',
