            51 => 
            array (
                'id' => 42312,
                'capitulo_id' => 1574,
                'pericopa_id' => 2680,
                'num_versiculo' => 66,
                'contenido' => 'Y oyó una voz del cielo que decía: Eres bautizado con fuego y con el Espíritu Santo. Éste es el testimonio del Padre y del Hijo, desde ahora y para siempre;',
                'referencia' => 'Moisés 6:66',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:47',
                'updated_at' => '2024-03-12 15:25:47',
            ),
            52 => 
            array (
                'id' => 42313,
                'capitulo_id' => 1574,
                'pericopa_id' => 2680,
                'num_versiculo' => 67,
                'contenido' => 'y eres según el orden de aquel que fue sin principio de días ni fin de años, de eternidad en eternidad.',
                'referencia' => 'Moisés 6:67',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:47',
                'updated_at' => '2024-03-12 15:25:47',
