            53 => 
            array (
                'id' => 42314,
                'capitulo_id' => 1574,
                'pericopa_id' => 2680,
                'num_versiculo' => 68,
                'contenido' => 'He aquí, eres uno en mí, un hijo de Dios; y así todos pueden llegar a ser mis hijos. Amén.',
                'referencia' => 'Moisés 6:68',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:47',
                'updated_at' => '2024-03-12 15:25:47',
