            140 => 
            array (
                'id' => 42401,
                'capitulo_id' => 1576,
                'pericopa_id' => 2693,
                'num_versiculo' => 16,
                'contenido' => 'Y aconteció que Noé profetizó y enseñó las cosas de Dios, aun como fue en el principio.',
                'referencia' => 'Moisés 8:16',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:47',
                'updated_at' => '2024-03-12 15:25:47',
            ),
