                'capitulo_id' => 1582,
                'pericopa_id' => 2737,
                'num_versiculo' => 8,
                'contenido' => 'y entonces se ofenderán muchos, y se traicionarán unos a otros, y se aborrecerán unos a otros;',
                'referencia' => 'José Smith-Mateo 1:8',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:47',
                'updated_at' => '2024-03-12 15:25:47',
